import * as React from "react";
import EmployeeList from './EmployeeList'

class AllocationList extends React.Component {
    employee = React.createRef();
    role = React.createRef();
    startMonth = React.createRef();
    endMonth = React.createRef();
    allocation = React.createRef();
    weight = React.createRef();
    addNewAllocation = (event) => {
        event.preventDefault();
        const allocationData = {'name': this.employee.current.value, 'role': this.role.current.value, 'project': this.props.projectTitle, 'startMonth': this.startMonth.current.value, 'endMonth': this.endMonth.current.value,'allocation': this.allocation.current.value, 'weight': this.weight.current.value,}; 
        this.props.addAllocation(allocationData);
    }
    render() {
        const project = this.props
        return (
			<div>
            <EmployeeList projectTitle = {project.title} employees = {project.employees} allocations = {project.allocations} addAllocation = {project.addAllocation}/>
			<form>
			<label>Employee: </label>
            <input type="text" list="employees" ref={this.employee}/>
            <datalist id="employees">
                {this.props.employees.map(employee => <EmployeeDropdown {...employee}/>)}
            </datalist>
            <label > Role: </label>
            <select type = "text" ref={this.role}>
                <option>BA</option>
                <option>QA</option>
                <option>Developer</option>
            </select>
            <label > Start Month: </label>
            <input type="month" min="2020-07" defaultValue="2020-07" ref={this.startMonth}/>
            <label> Allocation: </label>
            <label > End Month: </label>
            <input type="month" min="2020-07" defaultValue="2020-07" ref={this.endMonth}/>
            <label> Allocation: </label>
            <input 
            type="number" min="0" max="1" step="0.01" 
            ref = {this.allocation}
            required 
            />
            <label> Weight: </label>
            <input 
            type="number" min="0" max="1" step="0.01" 
            ref = {this.weight}
            required 
            />         
            <button onClick={this.addNewAllocation}>Add Allocation</button>
			</form>
			</div>
			);
		}
	}

class Allocation extends React.Component {
	render() {
  	const allocation = this.props;
  	return (
    	<div>
          <div>Role: {allocation.role} Months: {allocation.startMonth}-{allocation.endMonth} Allocation: {allocation.allocation} Weight: {allocation.weight}</div>
    	</div>
    );
  }
}

class EmployeeDropdown extends React.Component {
	render() {
  	const employee = this.props;
  	return (
        <option>{employee.firstName} {employee.lastName}</option>
    );
  }
}

export default EmployeesList;
