import * as React from "react";
import CapChart from "./CapChart";

function AddEmployee(props) {
	return (
    <button>5</button>
    // <form style={{textAlign: "center"}}>
    //     <label>Employee Name</label>
    //     <input 
    //     type="text"
    //     required 
    //     />
    // </form>
  );
}

class Form extends React.Component {
    titleInput = React.createRef();
    handleClick = (event) => {
        event.preventDefault();
        
    }
    handleSubmit = (event) => {
        event.preventDefault();
        return <AddEmployee />
    }
    render() {

        return (
            <form style={{textAlign: "center"}}>
            <h1>Add a Project</h1>
            <label>Project Title: </label>
            <input 
            type="text"
            ref = {this.titleInput}
            required 
            />
            <br/>
            <button onClick={this.handleClick}>Add Employee</button>
            <br/>
            <button onClick={this.handleSubmit}>Add Project</button>
            </form>
        );
    }
}

export default Form;
