import * as React from "react";
import CapChart from "./CapChart";

// const EmployeeList = (props) => (
// 	<div>
//   	{props.employees.map(employee => <Employee {...employee}/>)}
// 	</div>
// );

class Form extends React.Component {
    title = React.createRef();
    handleClick = (event) => {
        event.preventDefault();
             
    }
    handleSubmit = (event) => {
        event.preventDefault();
        // return <AddEmployee />

    }
    render() {

        return (
            <form style={{textAlign: "center"}}>
            <h1>Add a Project</h1>
            <label>Project Title: </label>
            <input 
            type="text"
            ref = {this.title}
            required 
            />
            <br /><br /><label>Start Date: </label>
            <input 
            type="date"
            ref = {this.startDate}
            required 
            />
            <label> End Date: </label>
            <input 
            type="date"
            ref = {this.endDate}
            required 
            />
            <br /><br /><label>BA Points: </label>
            <input 
            type="number"
            ref = {this.baPoints}
            required 
            />
            <label> QA Points: </label>
            <input 
            type="number"
            ref = {this.qaPoints}
            required 
            />
            <label > Developer Points: </label>
            <input 
            type="number"
            ref = {this.devPoints}
            required 
            />
            <br /><br /><label>Employee: </label>
            <select>
            <option>Michael</option>
            <option>Travis</option>
            <option>Julie</option>
            <option>Charlie</option>
            </select>
            <br /><br/><button onClick={this.handleClick}>Add Allocation</button>
            <br /><br/><button onClick={this.handleSubmit}>Add Project</button>
            {/* function call to return employees names */}
            
            </form>
        );
    }
}

export default Form;
