import * as React from "react";
import AllocationList from "./AllocationList";
import axios from 'axios';
class ProjectsList extends React.Component {
    title = React.createRef();
    startDate = React.createRef();
    endDate = React.createRef();
    totalPoints = React.createRef();
    baPoints = React.createRef();
    qaPoints = React.createRef();
    devPoints = React.createRef();
            //event.preventDefault();
        // const projectData = {
        //     'title': this.title.current.value,
        //     'startDate': '2020-01-01',
        //     'endDate': '2020-03-01', 
        //     'totalPoints' : this.totalPoints.current.value, 
        //     'baPoints': this.baPoints.current.value, 
        //     'qaPoints': this.qaPoints.current.value, 
        //     'devPoints': this.devPoints.current.value,
        // };
    async postProject(event) {

        event.preventDefault();
        try {
            const result = await fetch('https://localhost:44391/api/employees', {
                method: 'POST',
                headers: {
                    'Accept': 'application/json',
                    'Content-type':'application/json',
                },
                body: {
                    name: 'benson',
                    role: 'BA',
                }
            });

            console.log('Result ' + result)
        } catch (e) {
            console.log(e)
        }
        //this.props.refreshState();
    }
    render() {

        return (
            <div>
  	        {this.props.projects.map(project => <Project employees = {this.props.employees} allocationState = {this.props.allocations} addEmployee = {this.props.addEmployee} addAllocation = {this.props.addAllocation} {...project}/>)}
	        
            {/* Form */}
            <form onSubmit={this.postProject}>
            <label>Project Title: </label>
            <input 
            type="text"
            ref = {this.title}
            required 
            />
            
            {/* Dates */}
            <label> Start Date: </label>
            <input 
            type="date"
            ref = {this.startDate}
            required 
            />
            <label> End Date: </label>
            <input 
            type="date"
            ref = {this.endDate}
            required 
            />

            
            {/* Points */}
            <label>Total Points: </label>
            <input 
            type="number"
            ref = {this.totalPoints}
            required 
            />
            <label>BA Points: </label>
            <input 
            type="number"
            ref = {this.baPoints}
            required 
            />
            <label> QA Points: </label>
            <input 
            type="number"
            ref = {this.qaPoints}
            required 
            />
            <label > Developer Points: </label>
            <input 
            type="number"
            ref = {this.devPoints}
            required 
            />
            <button type = 'submit'>Add Project</button>
            </form>
            </div>

        );
    }
}

class Project extends React.Component {
	render() {
  	const project = this.props;
  	return (
    	<div>
            <div>{project.title} Start Date: {project.startDate} End Date: {project.endDate} Total Points: {project.totalPoints} BA Points: {project.baPoints} QA Points: {project.qaPoints} Dev Points: {project.devPoints}</div>
            <AllocationList  projectId = {project.id} employees = {project.employees} allocations = {project.allocationState} addAllocation = {project.addAllocation}/>
        </div>
    );
  }
}

export default ProjectsList;
