import * as React from "react";
import CapChart from "./CapChart";

const EmployeeList = (props) => (
	<div>
  	{props.employees.map(employee => <Employee key={name} {...employee}/>)}
	</div>
);

class Form extends React.Component {
    titleInput = React.createRef();
    handleClick = (event) => {
        event.preventDefault();
        return <AddEmployee />        
    }
    handleSubmit = (event) => {
        event.preventDefault();
        return <AddEmployee />
    }
    render() {

        return (
            <form style={{textAlign: "center"}}>
            <h1>Add a Project</h1>
            <label>Project Title: </label>
            <input 
            type="text"
            ref = {this.titleInput}
            required 
            />
            <br/><button onClick={this.handleClick}>Add Employee</button>
            <br/><button onClick={this.handleSubmit}>Add Project</button>
            </form>
        );
    }
}

export default Form;
