import React from 'react';
import CapChart from './CapChart';
class BreakdownChart extends React.Component {
    constructor(props) {
      super(props)
      this.state = {
        baData: [], qaData:[], devData: [
        ]
      }
      this.refreshState = this.refreshState.bind(this);
    }
  
    async refreshState() {
      fetch('https://localhost:44391/api/projects/duration/' + this.props.id)
        .then(res => res.json())
        .then(json => {
            this.setState({
                baData: [
                  'BA',
                  'BA',
                  'BA',
                  new Date(this.props.startDate),
                  new Date(json.baEndDate),
                  null,
                  null,
                  null,
                ],
                qaDate: [
                  'QA',
                  'QA',
                  'QA',
                  this.props.startDate,
                  new Date(json.qaEndDate),
                  null,
                  null,
                  null,
                ],
                devData: [
                  'Dev',
                  'Dev',
                  'Dev',
                  this.props.startDate,
                  new Date(json.devEndDate),
                  null,
                  null,
                  null,
                ] 
                });
          },
        )
    }
  
    componentDidMount () {
      this.refreshState();
      const chartData = [this.props.chartSettings, this.state.baData, this.state.qaData, this.state.devData,];
    }

    
    render() {   
      return (   
        <div>     
        <CapChart data = {[this.props.chartSettings, this.state.baData, this.state.qaData, this.state.devData,]} />
        </div>
      )
    }
  }
  
  export default BreakdownChart;
