import * as React from "react";

class EmployeesList extends React.Component {
    title = React.createRef();
    startDate = React.createRef();
    endDate = React.createRef();
    baPoints = React.createRef();
    qaPoints = React.createRef();
    devPoints = React.createRef();
    employee = React.createRef();
    role = React.createRef();
    month = React.createRef();
    allocation = React.createRef();
    weight = React.createRef();
    addNewProject = (event) => {
        event.preventDefault();
        const projectData = {'title': this.title.current.value, 'startDate': this.startDate.current.value, 'enadDate': this.endDate.current.value, 'baPoints': this.baPoints.current.value, 'qaPoints': this.qaPoints.current.value, 'devPoints': this.devPoints.current.value,};
        this.props.addProject(projectData);
    }
    addNewAllocation = (event) => {
        event.preventDefault();
        const allocationData = {'name': this.employee.current.value, 'role': this.role.current.value, 'month': this.role.current.value, 'allocation': this.allocation.current.value, 'weight': this.weight.current.value,}; 
        this.props.addAllocation(allocationData);
    }
    render() {

        return (
			<div>
			{this.props.employees.map(employee => <Employee {...employee}/>)}

			<form>
			<label>Employee: </label>
            <input type="text" list="employees" ref={this.employee}/>
            <datalist id="employees">
                <option>Michael</option>
                <option>Travis</option>
                <option>Julie</option>
                <option>Charlie</option>
            </datalist>
            <label > Role: </label>
            <select type = "text" ref={this.role}>
                <option>BA</option>
                <option>QA</option>
                <option>Developer</option>
            </select>
            <label > Month: </label>
            <input type="month" min="2020-07" defaultValue="2020-07" ref={this.month}/>
            <label> Allocation: </label>
            <input 
            type="number" min="0" max="1" step="0.01" 
            ref = {this.allocation}
            required 
            />
            <label> Weight: </label>
            <input 
            type="number" min="0" max="1" step="0.01" 
            ref = {this.weight}
            required 
            />         
            <br /><br/><button onClick={this.addNewAllocation}>Add Allocation</button> */
			</form>
			</div>
			);
		}
	}

class Employee extends React.Component {
	render() {
  	const employee = this.props;
  	return (
    	<div>
          <div>Employee: {employee.name} Role: {employee.role} Month: {employee.month} Allocation: {employee.allocation} Weight: {employee.weight}</div>
    	</div>
    );
  }
}

export default EmployeesList;
