import * as React from "react";
import CapChart from "./CapChart";
import EmployeeList from "./EmployeeList";

class ProjectsList extends React.Component {
    title = React.createRef();
    startDate = React.createRef();
    endDate = React.createRef();
    baPoints = React.createRef();
    qaPoints = React.createRef();
    devPoints = React.createRef();
    addNewProject = (event) => {
        event.preventDefault();
        const projectData = {'title': this.title.current.value,  'startDate': this.startDate.current.value, 'endDate': this.endDate.current.value, 'baPoints': this.baPoints.current.value, 'qaPoints': this.qaPoints.current.value, 'devPoints': this.devPoints.current.value,};
        this.props.addProject(projectData);
    }
    render() {

        return (
            <div>
            
  	        {this.props.projects.map(project => <Project employees = {this.props.employees} addAllocation = {this.props.addAllocation} {...project}/>)}
	        
            {/* Form */}
            <form>
            <label>Project Title: </label>
            <input 
            type="text"
            ref = {this.title}
            required 
            />
            
            {/* Dates */}
            <label> Start Date: </label>
            <input 
            type="date"
            ref = {this.startDate}
            required 
            />
            <label> End Date: </label>
            <input 
            type="date"
            ref = {this.endDate}
            required 
            />

            
            {/* Points */}
            <label>BA Points: </label>
            <input 
            type="number"
            ref = {this.baPoints}
            required 
            />
            <label> QA Points: </label>
            <input 
            type="number"
            ref = {this.qaPoints}
            required 
            />
            <label > Developer Points: </label>
            <input 
            type="number"
            ref = {this.devPoints}
            required 
            />
            <button onClick={this.addNewProject}>Add Project</button>
            </form>
            </div>

        );
    }
}

class Project extends React.Component {
	render() {
  	const project = this.props;
  	return (
    	<div>
            <div>Title: {project.title} Start Date: {project.startDate} End Date: {project.endDate} BA Points {project.baPoints} QA Points {project.qaPoints} Dev Points {project.devPoints}</div>
            <EmployeeList employees = {project.employees} addAllocation = {this.props.addAllocation}/>
        </div>
    );
  }
}

export default ProjectsList;
