import * as React from "react";
import AllocationList from "./AllocationList";
class ProjectList extends React.Component {
    constructor(props) {
        super(props)
        this.state = {
            title : '',
            startDate : '',
            endDate : '',
            totalPoints: '',
            baPoints : '',
            qaPoints : '',
            devPoints : ''
        }
        this.postProject = this.postProject.bind(this);
    }

    changeHandler = (event) => {
        this.setState({[event.target.name]: event.target.value})
    }
    
    async postProject(event) {
        event.preventDefault();
        try {
            const result = await fetch('https://localhost:44391/api/projects', {
                method: 'post',
                headers: {
                    'Accept': 'application/json',
                    'Content-type':'application/json',
                },
                body: JSON.stringify(this.state)
            });

            console.log('Result ' + result)
        } catch (e) {
            console.log(e)
        }
        this.props.refreshState();
    }
    render() {

        return (
            <div>
	        
            {/* Form */}
            <form onSubmit={this.postProject} style={{
            backgroundColor: '#d3e4ee',
            }}>
            <label>Project Title: </label>
            <input 
            type="text"
            name = 'title'
            value = {this.state.title}
            onChange = {this.changeHandler}
            required 
            autoComplete="off"
            />
            
            {/* Dates */}
            <label> Start Date: </label>
            <input 
            type="date"
            name = 'startDate'
            value = {this.state.startDate}
            onChange = {this.changeHandler}
            required 
            />
            <label> End Date: </label>
            <input 
            type="date"
            name = 'endDate'
            value = {this.state.endDate}
            onChange = {this.changeHandler}
            />

            
            {/* Points */}
            <label>Total Points: </label>
            <input 
            type="number"
            name = 'totalPoints'
            value = {this.state.totalPoints}
            onChange = {this.changeHandler}
            required 
            />
            <br /><label>BA Points: </label>
            <input 
            type="number"
            name = 'baPoints'
            value = {this.state.baPoints}
            onChange = {this.changeHandler}
            required 
            />
            <label> QA Points: </label>
            <input 
            type="number"
            name = 'qaPoints'
            value = {this.state.qaPoints}
            onChange = {this.changeHandler}
            required 
            />
            <label > Developer Points: </label>
            <input 
            type="number"
            name = 'devPoints'
            value = {this.state.devPoints}
            onChange = {this.changeHandler}
            required 
            />
            <button type = 'submit'>Add Project</button>
            </form>
            </div>

        );
    }
}


class Project extends React.Component {
    constructor(props) {
        super(props)
        this.state = {
            isEditing: false
        }
        this.deleteProject = this.deleteProject.bind(this);
    }

    async deleteProject(id) {
        if(window.confirm('Are you sure')) {
            
            try { 
                const result = await fetch('https://localhost:44391/api/projects/' + this.props.id, {
                    method: 'delete',
                    headers: {
                        'Accept': 'application/json',
                        'Content-type':'application/json',
                    }
                })
    
                console.log('Result ' + result)
            } catch (e) {
                console.log(e)
            }
            this.props.refreshState();
        }
    }
	render() {
      const project = this.props;

    if (this.state.isEditing) {
        return (
            <div></div>
        )
    }  else {
        return (
            <div>
                <div style = {{backgroundColor : '#ddd3ee'}}>Project: {project.title} Start Date: {project.startDate.substring(5,7)}/{project.startDate.substring(8,10)}/{project.startDate.substring(0,4)} End Date: {project.endDate.substring(5,7)}/{project.endDate.substring(8,10)}/{project.endDate.substring(0,4)} Total Points: {project.totalPoints} BA Points: {project.baPoints} QA Points: {project.qaPoints} Dev Points: {project.devPoints}
                <button>Update</button>
                <button onClick={this.deleteProject}><img src="https://icon-library.com/images/delete-icon-png-16x16/delete-icon-png-16x16-21.jpg" alt="my image" width="12" height="12"/></button>
                </div>
                <AllocationList refreshState = {this.props.refreshState} projectId = {project.id} employees = {project.employees} allocations = {project.allocationState} addAllocation = {project.addAllocation}/>
            </div>
        );
    }

  	
  }
}

export { ProjectList, Project, };
