import * as React from "react";

class Form extends React.Component {
    titleInput = React.createRef();
    render() {
        return (
            <form onSubmit={this.handleSubmit}> 
            <input 
            type="text"
            placeholder="Project title" 
            ref = {this.titleInput}
            required 
            />
            <button>Add Project</button>
            </form>
        );
    }
}

export default Form;
