import * as React from "react";
import AllocationList from "./AllocationList";
class ProjectForm extends React.Component {
    constructor(props) {
        super(props)
        this.state = {
            title : '',
            startDate : '',
            endDate : '',
            totalPoints: '',
            baPoints : '',
            qaPoints : '',
            devPoints : '',
        }
        this.postProject = this.postProject.bind(this);
        this.putProject = this.putProject.bind(this);
        this.fillState = this.fillState.bind(this);
    }

    changeHandler = (event) => {
        this.setState({[event.target.name]: event.target.value})
    }
    
    async postProject(event) {
        event.preventDefault();
        try {
            const result = await fetch('https://localhost:44391/api/projects', {
                method: 'post',
                headers: {
                    'Accept': 'application/json',
                    'Content-type':'application/json',
                },
                body: JSON.stringify(this.state)
            });

            console.log('Result ' + result)
        } catch (e) {
            console.log(e)
        }
        this.props.refreshState();
    }

    // convert(str) {
    //     var date = new Date(str),
    //       mnth = ("0" + (date.getMonth() + 1)).slice(-2),
    //       day = ("0" + date.getDate()).slice(-2);
    //     return [date.getFullYear(), mnth, day].join("-");
    //   }

    fillState() {
        var event1 = new Date(this.props.startDate);
        let startDate = JSON.stringify(event1)
        startDate = startDate.slice(1,11)
        var event2 = new Date(this.props.endDate);
        let endDate = JSON.stringify(event2)
        endDate = endDate.slice(1,11)
        this.setState({
            title : this.props.title,
            startDate : startDate,
            endDate : endDate,
            totalPoints: this.props.totalPoints,
            baPoints : this.props.baPoints,
            qaPoints : this.props.qaPoints,
            devPoints : this.props.devPoints,
        })
    }

    componentDidMount() {
        if (this.props.isEditing) {
            this.fillState();
        }
    };

    async putProject(event) {
        event.preventDefault();
        try {
            const result = await fetch('https://localhost:44391/api/projects/' + this.props.projectId, {
                method: 'put',
                headers: {
                    'Accept': 'application/json',
                    'Content-type':'application/json',
                    'Access-Control-Allow-Credentials': true
                },
                body: JSON.stringify(this.state)
            });

            console.log('Result ' + result)
        } catch (e) {
            console.log(e)
        }
        this.props.refreshState();
        this.props.toggleEdit();
    }

    render() {

        return (
            <div>
	        
            {/* Form */}
            <form onSubmit={this.props.isEditing ? this.putProject : this.postProject} style={{
            backgroundColor: '#d3e4ee',
            }}>
            <label>Project Title: </label>
            <input 
            type="text"
            name = 'title'
            value = {this.state.title}
            onChange = {this.changeHandler}
            required 
            autoComplete="off"
            />
            
            {/* Dates */}
            <label> Start Date: </label>
            <input 
            type="date"
            name = 'startDate'
            value = {this.state.startDate}
            onChange = {this.changeHandler}
            required 
            />
            <label> End Date: </label>
            <input 
            type="date"
            name = 'endDate'
            value = {this.state.endDate}
            onChange = {this.changeHandler}
            />

            
            {/* Points */}
            <label>Total Points: </label>
            <input 
            type="number"
            name = 'totalPoints'
            value = {this.state.totalPoints}
            onChange = {this.changeHandler}
            required 
            />
            <br /><label>BA Points: </label>
            <input 
            type="number"
            name = 'baPoints'
            value = {this.state.baPoints}
            onChange = {this.changeHandler}
            required 
            />
            <label> QA Points: </label>
            <input 
            type="number"
            name = 'qaPoints'
            value = {this.state.qaPoints}
            onChange = {this.changeHandler}
            required 
            />
            <label > Developer Points: </label>
            <input 
            type="number"
            name = 'devPoints'
            value = {this.state.devPoints}
            onChange = {this.changeHandler}
            required 
            />
            
            {this.props.isEditing ? <span><button onClick = {this.props.toggleEdit}>Cancel</button><button type = 'submit'>Confirm</button></span> : <button type = 'submit'>Add Project</button>}
            </form>
            </div>

        );
    }
}


class Project extends React.Component {
    constructor(props) {
        super(props)
        this.state = {
            isEditing: false
        }
        this.deleteProject = this.deleteProject.bind(this);
        this.toggleEdit = this.toggleEdit.bind(this);
    }
    toggleEdit() {
        this.setState ({
            isEditing: !this.state.isEditing
        })
    }

    async deleteProject(id) {
        if(window.confirm('Are you sure')) {
            
            try { 
                const result = await fetch('https://localhost:44391/api/projects/' + this.props.id, {
                    method: 'delete',
                    headers: {
                        'Accept': 'application/json',
                        'Content-type':'application/json',
                    }
                })
    
                console.log('Result ' + result)
            } catch (e) {
                console.log(e)
            }
            this.props.refreshState();
        }
    }
	render() {
      const project = this.props;

    if (this.state.isEditing) {
        return (
            <div>
                <ProjectForm 
                    refreshState = {this.props.refreshState}
                    isEditing = {this.state.isEditing} 
                    toggleEdit = {this.toggleEdit}
                    projectId = {this.props.id}
                    title = {this.props.title}
                    startDate = {this.props.startDate}
                    endDate = {this.props.endDate}
                    totalPoints = {this.props.totalPoints}
                    baPoints = {this.props.baPoints}
                    qaPoints = {this.props.qaPoints}
                    devPoints = {this.props.devPoints}
                />

                <AllocationList 
                    refreshState = {this.props.refreshState}
                    projectId = {project.id} 
                    employees = {project.employees} 
                    allocations = {project.allocationState}
                />
            </div>
        )
    }  else {
        return (
            <div>
                <div style = {{backgroundColor : '#ddd3ee'}}>Project: {project.title} Start Date: {project.startDate.substring(5,7)}/{project.startDate.substring(8,10)}/{project.startDate.substring(0,4)} End Date: {project.endDate.substring(5,7)}/{project.endDate.substring(8,10)}/{project.endDate.substring(0,4)} Total Points: {project.totalPoints} BA Points: {project.baPoints} QA Points: {project.qaPoints} Dev Points: {project.devPoints}
                <button onClick = {this.toggleEdit}>Update</button>
                <button onClick={this.deleteProject}><img src="https://icon-library.com/images/delete-icon-png-16x16/delete-icon-png-16x16-21.jpg" alt="my image" width="12" height="12"/></button>
                </div>
                <AllocationList refreshState = {this.props.refreshState} projectId = {project.id} employees = {project.employees} allocations = {project.allocationState} />
            </div>
        );
    }

  	
  }
}

export { ProjectForm, Project, };
