import React from 'react';
import ReactDOM from 'react-dom';
import './index.css'
import CapChart from './CapChart';
import BreakdownChart from './BreakdownChart';
import ProjectsList from './ProjectsList';
import * as serviceWorker from './serviceWorker';

const chartSettings = [
    [
      { type: 'string', label: 'Task ID' },
      { type: 'string', label: 'Task Name' },
      { type: 'string', label: 'Resource' },
      { type: 'date', label: 'Start Date' },
      { type: 'date', label: 'End Date' },
      { type: 'number', label: 'Duration' },
      { type: 'number', label: 'Percent Complete' },
      { type: 'string', label: 'Dependencies' },
    ], 
    [
      'EEL',
      'EEL',
      null,
      new Date(2020, 5, 1),
      new Date(2020, 7, 14),
      null,
      100,
      null,
    ],
  ];

class App extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      chartData: [
        [
        { type: 'string', label: 'Task ID' },
        { type: 'string', label: 'Task Name' },
        { type: 'string', label: 'Resource' },
        { type: 'date', label: 'Start Date' },
        { type: 'date', label: 'End Date' },
        { type: 'number', label: 'Duration' },
        { type: 'number', label: 'Percent Complete' },
        { type: 'string', label: 'Dependencies' },
      ],
      ], projects: [], allocations: [], employees: []
    };
  }
  refreshState = () => {
      fetch('https://localhost:44391/api/projects')
      .then(res => res.json())
      .then(json => {
          const projectData = [this.state.chartData[0],];

          json.map(project =>
              projectData.push([
                  project.id,
                  project.title,
                  null,
                  new Date(project.startDate),
                  new Date(project.calcEndDate),
                  null,
                  null,
                  null,
              ])
           )


          this.setState({
            projects: json,
            chartData: projectData
          });
        }
      )
      fetch('https://localhost:44391/api/employees')
      .then(res => res.json())
      .then(json => {
          this.setState({
              employees: json
          });
        },
      )
      fetch('https://localhost:44391/api/allocations')
      .then(res => res.json())
      .then(json => {
          this.setState({
              allocations: json,
              });
        },
      )

  }

  componentDidMount() {
    this.refreshState();
  };

	render() {
  	return (
    	<div>
        <div><h1 style={{textAlign: "center"}}>{this.props.title}</h1></div>
        <CapChart data={this.state.chartData} />
        {this.state.loading ? <div>loading...</div> : <ProjectsList allocations = {this.state.allocations} employees = {this.state.employees} projects = {this.state.projects} refreshState={this.refreshState} addAllocation={this.addAllocation} addEmployee={this.addEmployee}/>}
        {this.state.projects.map(project => <BreakdownChart chartSettings = {[
        { type: 'string', label: 'Task ID' },
        { type: 'string', label: 'Task Name' },
        { type: 'string', label: 'Resource' },
        { type: 'date', label: 'Start Date' },
        { type: 'date', label: 'End Date' },
        { type: 'number', label: 'Duration' },
        { type: 'number', label: 'Percent Complete' },
        { type: 'string', label: 'Dependencies' },
        ]} {...project}/>)}
      </div>
    );
  }	
}

ReactDOM.render(
  <App title = 'Personal Lines Capacity Management'/>,
  document.getElementById('root')
);

// If you want your app to work offline and load faster, you can change
// unregister() to register() below. Note this comes with some pitfalls.
// Learn more about service workers: https://bit.ly/CRA-PWA
serviceWorker.unregister();
