import * as React from "react";
import Allocation from "./AllocationList";

class EmployeeList extends React.Component {
    render() {
        return (
			<div>
  	            {this.props.employees.map(employee => {
                    if (employee.projects.includes(this.props.projectTitle))
                    return <div>{employee.firstName} {employee.lastName}
                            <br/>
                            {this.props.allocations.filter(allocation =>
                            allocation.name === employee && 
                            allocation.project === this.props.projectTitle)
                            .map(allocation => <Allocation />                  
                            )}
                            </div>
                            }
                )}
			</div>
			);
		}
	}

export default EmployeeList;
