import * as React from "react";
import CapChart from "./CapChart";

function addEmployee(props) {
	return (
  	<div>
    <form style={{textAlign: "center"}}>
        <label>Employee Name</label>
        <input 
        type="text"
        required 
        />
    </form>
    </div>
  );
}

class Form extends React.Component {
    titleInput = React.createRef();
    handleClick = (event) => {
        
        return <addEmployee />
    }
    handleSubmit = (event) => {
        event.preventDefault();
    }
    render() {

        return (
            <form style={{textAlign: "center"}}>
            <h1>Add a Project</h1>
            <label>Project Title: </label>
            <input 
            type="text"
            ref = {this.titleInput}
            required 
            />
            <br/>
            <button onClick={this.handleClick}>Add Employee</button>
            <br/>
            <button onClick={this.handleSubmit}>Add Project</button>
            </form>
        );
    }
}

export default Form;
