import * as React from "react";
import CapChart from "./CapChart";

const EmployeeList = (props) => (
	<div>
  	{props.employees.map(employee => <Employee {...employee}/>)}
	</div>
);

class Form extends React.Component {
    title = React.createRef();
    handleClick = (event) => {
        event.preventDefault();
             
    }
    handleSubmit = (event) => {
        event.preventDefault();
        return <AddEmployee />
    }
    render() {

        return (
            <form style={{textAlign: "center"}}>
            <h1>Add a Project</h1>
            <label>Project Title: </label>
            <input 
            type="text"
            ref = {this.title}
            required 
            />
            <br /><label>Start Date: </label>
            <input 
            type="date"
            ref = {this.startDate}
            required 
            />
            <label>End Date: </label>
            <input 
            type="date"
            ref = {this.endDate}
            required 
            />
            <br /><label>BA Points: </label>
            <input 
            type="number"
            ref = {this.baPoints}
            required 
            />
            <label>QA Points: </label>
            <input 
            type="number"
            ref = {this.qaPoints}
            required 
            />
            <label>Developer Points: </label>
            <input 
            type="number"
            ref = {this.devPoints}
            required 
            />
            <br/><button onClick={this.handleClick}>Add Employee</button>
            <br/><button onClick={this.handleSubmit}>Add Project</button>
            </form>
        );
    }
}

export default Form;
